import React, { useState } from 'react';
import PropTypes from 'prop-types';
import '../App/App.css';
import axios from 'axios';

const initialValues = {
	/* -- basic information -- */
    k: '',
	maturity_date: '',
	delta: '',
	pi: '',

	/* -- Callable Bond Information -- */
	coupon_rate: '',
	face_value: '',
	day_count_convention: '0',

	/* -- Zero Coupon Bond Term Structure -- */
	bonds: [
        { id: 1, time_to_maturity: '', price: '' }
	]
}

const BondForm = ({ change }) => {
	const [state, setState] = useState(initialValues);

	const handleChange = e => {
		let { value, name } = e.target;
		// if (value > 999) {
		// 	value = 999;
		// }
		setState({
			...state,
			[name]: value
		});
	};

	const [file, setFile] = useState(null);

    const handleFileChange = (event) => {
        setFile(event.target.files[0]); // Set the file to state
    };

	// const handleChange = e => {
    //     const { name, value } = e.target;
    //     let newState = { ...state };
    //     if (name.startsWith('time_to_maturity') || name.startsWith('price')) {
    //         const index = parseInt(name.split('-')[1], 10);
    //         const fieldName = name.split('-')[0];
    //         newState.bonds[index][fieldName] = value;
    //     } else {
    //         newState[name] = value > 999 ? 999 : value;
    //     }
    //     setState(newState);
    // };

	const handleSubmit = () => {
		change(state);
		setState(initialValues);
	};

	return (
		<>
			<div className="row">
				<div className='row center'>
					<h4 className='white-text'> Basic Information </h4>
				</div>

				<div className="center col s3">
					<label htmlFor="k"> Strike Price </label>
					<input
						id="k"
						name="k"
						type="number"
						min="0.00"
						max="200.00"
						step="0.01"
						placeholder="100.00"
						value={state.k}
						onChange={handleChange}
					/>
				</div>

				<div className="center col s3">
					<label htmlFor="maturity_date"> Maturity Date </label>
					<input
						id="maturity_date"
						name="maturity_date"
						type="date"
						value={state.maturity_date}
						onChange={handleChange}
					/>
				</div>


				<div className="center col s3">
					<label htmlFor="delta"> Delta </label>
					<input
						id="delta"
						name="delta"
						type="number"
						min="0.00"
						max="1.00"
						step="0.01"
						placeholder="0.90"
						value={state.delta}
						onChange={handleChange}
					/>
				</div>

				<div className="center col s3">
					<label htmlFor="pi">Pi </label>
					<input
						id="pi"
						name="pi"
						type="number"
						min="0"
						max="1.00"
						placeholder="0.50"
						value={state.pi}
						onChange={handleChange}
					/>
				</div>

				<br/><br/><br/><br/>
				<div className='row center'>
					<h4 className='white-text'> Callable Bond Information </h4>
				</div>

				<div className="center col s3">
					<label htmlFor="coupon_rate">Coupon Rate (as Decimal) </label>
					<input
						id="coupon_rate"
						name="coupon_rate"
						type="number"
						min="0"
						max="1.00"
						step="0.01"
						placeholder="0.50"
						value={state.coupon_rate}
						onChange={handleChange}
					/>
				</div>
				<div className="center col s3">
					<label htmlFor="face_value">Face Value </label>
					<input
						id="face_value"
						name="face_value"
						type="number"
						min="0"
						max="100.00"
						placeholder="50.00"
						value={state.face_value}
						onChange={handleChange}
					/>
				</div>
				<div className="center col s3">
					<label htmlFor="day_count_convention">Day Count Convention </label>
					<select
						id="day_count_convention"
						name="day_count_convention"
						value={state.day_count_convention}
						onChange={handleChange}
					>
						<option value="0">Thirty/360</option>
						<option value="1">Thirty/365</option>
						<option value="2">Actual/360</option>
						<option value="3">Actual/365</option>
						<option value="4">Actual/Actual</option>
					</select>
				</div>

				<br/><br/><br/><br/>
				<div className="row center">
                <h4 className='white-text'>Zero Coupon Bond Term Structure</h4>
				<h5 className='white-text'>Upload File </h5>
					<form>
						<input type="file" />
					</form>
				</div>

			</div>

			<div className="center">
				<button
					id="bond-btn"
					className="calculate-btn"
					type="button"
					disabled={state.r0 === '' || 
							  state.k  === '' || 
							  state.maturity_date === '' || 
							  state.delta === '' || 
							  state.pi === '' || 
							  state.coupon_rate === '' || 
							  state.face_value === '' || 
							  state.day_count_convention === '' || 
							  state.callable_bond_price === ''}
					onClick={handleSubmit}
				>
					Calculate Bond Price
				</button>
			</div>
		</>
	);
};

BondForm.propTypes = {
	change: PropTypes.func.isRequired
};

export default BondForm;
